import { useState } from 'react';

function Square({value,onSquareClick}) {
  // const [value,setValue] = useState("0");
  // function handleClick(){
  //   setValue('X');
  //   console.log(value);
  // }

  return (<button className="square" onClick={onSquareClick}>{value}</button>);
}
export default function Board() {
  //Make userX as the default active User
  const [activeUser,setActiveUser] = useState('userX');
  //Initiate the board with 9 squares filled with null
  const [squares,setSquares] = useState(Array(9).fill(null));

  function handleSquareClick(i){
    //If the square has value. i.e it has been clicked once already. Do not change it.
    // return from here so the square is unclickable.
    if(squares[i]){
      return;
    }
    //make a copy of original state
    const nextSquares = squares.slice();

    if(activeUser === 'userX'){
      nextSquares[i] = "X";
      setActiveUser('userO');
    }      
    else {
      nextSquares[i] = "O";  
      setActiveUser('userX');
    }

    setSquares(nextSquares);
  }

  return (
    <>
    <div className="board-row">
      <Square value={squares[0]} onSquareClick={() => handleSquareClick(0)} />
      <Square value={squares[1]} onSquareClick={() => handleSquareClick(1)} />
      <Square value={squares[2]} onSquareClick={() => handleSquareClick(2)} />
    </div>
    <div className="board-row">
      <Square value={squares[3]} onSquareClick={() => handleSquareClick(3)} />
      <Square value={squares[4]} onSquareClick={() => handleSquareClick(4)} />
      <Square value={squares[5]} onSquareClick={() => handleSquareClick(5)} />
    </div>
    <div className="board-row">
      <Square value={squares[6]} onSquareClick={() => handleSquareClick(6)} />
      <Square value={squares[7]} onSquareClick={() => handleSquareClick(7)} />
      <Square value={squares[8]} onSquareClick={() => handleSquareClick(8)} />
    </div>

    </>
  )
}
